import React from 'react';
import './App.css';
import data from './data.json';
import boyImg from './assets/boy.png';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      currentSong: data[0].songs[0].song,
      playList: data[0],
      currentSelectedPlayList: data[0].playlist,
      currentSongPLayList: data[0].playlist
    }
  }
  currentSongplay(e, d) {
    console.log("e", e.playlist, d.song);
    this.setState({
      currentSong: d.song,
      currentSongPLayList: e.playlist
    })
  }
  changePlayList(e) {
    console.log(e);
    this.setState({
      currentSelectedPlayList: e.playlist,
      playList: e
    })
  }
  render() {
    console.log(this.state.currentSongPLayList, '======', this.state.playList);
    return (
      <div>
        <div className="slider">
          <div className="sliderContent">
            <div className="col-md-6">
              <img src={boyImg} />
            </div>
            <div className="col-md-6 sliderRight">
              <div className="text">
                <h2>Meet our brand </h2>
                <p>Lorem Ipsum is simply dummy<br />
                  text of the printing and typesetting industry.<br />
                  Lorem Ipsum has been the industry's standard dummy<br />
                  text ever since the 1500s, when an unknown printer</p>
              </div>
            </div>
          </div>
        </div>
        <div className="playList">
          <div className="col-md-6">
            {data.map((data, key) => (<p key={key} onClick={() => this.changePlayList(data)}>{this.state.currentSelectedPlayList == data.playlist ? ">" : null}{data.playlist}</p>))}

          </div>
          <div className="col-md-6">
            {this.state.playList.songs.map((d, key) => (<p key={key} onClick={() => this.currentSongplay(this.state.playList, d)}>{this.state.currentSong == d.song ? ">" : null}{d.song}</p>))}

          </div>
        </div>

        <br /><br />
        <div>
          {this.state.currentSongPLayList}<br />{this.state.currentSong}
        </div>
      </div>
    );
  }
}

export default App;
